Bash(node -e "
const http = require\(''http''\);
const fs = require\(''fs''\);
const path = require\(''path''\);

const server = http.createServer\(\(req, res\) => {
  const filePath = path.join\(''.'', req.url === ''/'' ? ''synth.html'' : req.url\);
  const ext = path.extname\(filePath\);
  const types = {''.html'':''text/html'',''.js'':''application/javascript'',''.css'':''text/css''};
  try {
    const data = fs.readFileSync\(filePath\);
    res.writeHead\(200, {''Content-Type'': types[ext] || ''text/plain''}\);
    res.end\(data\);
  } catch\(e\) {
    res.writeHead\(404\); res.end\(''Not found''\);
  }
}\);
server.listen\(8767\);

const puppeteer = require\(require\(''child_process''\).execSync\(''npm root -g''\).toString\(\).trim\(\) + ''/puppeteer''\);
\(async \(\) => {
  const browser = await puppeteer.launch\({headless: ''new''}\);
  const page = await browser.newPage\(\);
  const errors = [];
  page.on\(''console'', msg => { if \(msg.type\(\) === ''error''\) errors.push\(msg.text\(\)\); }\);
  page.on\(''pageerror'', err => errors.push\(err.message\)\);
  await page.goto\(''http://localhost:8767/synth.html'', {waitUntil: ''networkidle0''}\);
  await new Promise\(r => setTimeout\(r, 1000\)\);
  console.log\(JSON.stringify\(errors, null, 2\)\);
  await browser.close\(\);
  server.close\(\);
}\)\(\);
")
